import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ChatBot = () => {
  const [chats, setChats] = useState([]);
  const [currentChatId, setCurrentChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  const fetchChatHistory = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:3000/api/chat', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setChats(response.data);
    } catch (err) {
      console.error('Error fetching chat history:', err);
    }
  };

  const startNewChat = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:3000/api/chat',
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setCurrentChatId(response.data._id);
      setMessages([]);
    } catch (err) {
      console.error('Error starting new chat:', err);
    }
  };

  const sendMessage = async () => {
    if (!input.trim() || !currentChatId) return;

    const newMessage = { role: 'user', content: input };
    setMessages((prev) => [...prev, newMessage]);
    setInput('');
    setLoading(true);

    try {
      const token = localStorage.getItem('token');
      await axios.post(
        `http://localhost:3000/api/chat/${currentChatId}/message`,
        newMessage,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const response = await axios.post(
        'https://api.openai.com/v1/chat/completions',
        {
          model: 'gpt-3.5-turbo',
          messages: [...messages, newMessage],
        },
        {
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer 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`,
          },
        }
      );

      const botMessage = {
        role: 'assistant',
        content: response.data.choices[0].message.content,
      };

      await axios.post(
        `http://localhost:3000/api/chat/${currentChatId}/message`,
        botMessage,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setMessages((prev) => [...prev, botMessage]);
    } catch (err) {
      console.error('Error communicating with OpenAI:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchChatHistory();
  }, []);

  return (
    <div className="flex flex-col items-center p-6">
      <h1 className="text-3xl font-bold mb-6">AI ChatBot</h1>
      <button
        onClick={startNewChat}
        className="mb-4 bg-blue-500 text-white px-4 py-2 rounded"
      >
        Start New Chat
      </button>
      <div className="flex">
        <div className="w-1/3 p-4 border-r">
          <h2 className="font-bold mb-4">Chat History</h2>
          {chats.map((chat, idx) => (
            <div
              key={idx}
              className="cursor-pointer mb-2 p-2 border rounded hover:bg-gray-200"
              onClick={() => {
                setCurrentChatId(chat._id);
                setMessages(chat.messages);
              }}
            >
              Chat {idx + 1} - {new Date(chat.createdAt).toLocaleDateString()}
            </div>
          ))}
        </div>
        <div className="w-2/3 p-4">
          <div className="h-96 overflow-y-scroll border mb-4">
            {messages.map((msg, idx) => (
              <div
                key={idx}
                className={`p-2 ${
                  msg.role === 'user'
                    ? 'text-right bg-blue-100'
                    : 'text-left bg-gray-100'
                }`}
              >
                {msg.content}
              </div>
            ))}
          </div>
          <div className="flex">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type your message..."
              className="flex-1 border px-4 py-2 rounded"
            />
            <button
              onClick={sendMessage}
              className={`ml-4 px-4 py-2 rounded ${
                loading
                  ? 'bg-gray-500 cursor-not-allowed'
                  : 'bg-green-500 text-white hover:bg-green-600'
              }`}
              disabled={loading}
            >
              {loading ? 'Loading...' : 'Send'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatBot;
