import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ChatBot = () => {
  const [chats, setChats] = useState([]);
  const [currentChatId, setCurrentChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  // Fetch chat history
  const fetchChatHistory = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/chat');
      setChats(response.data);
    } catch (err) {
      console.error('Error fetching chat history:', err);
    }
  };

  // Start a new chat
  const startNewChat = async () => {
    try {
      const response = await axios.post('http://localhost:3000/api/chat');
      setCurrentChatId(response.data.id);
      setMessages([]);
    } catch (err) {
      console.error('Error starting new chat:', err);
    }
  };

  // Send a message
  const sendMessage = async () => {
    if (!input.trim() || !currentChatId) return;

    const newMessage = { role: 'user', content: input };
    setMessages((prev) => [...prev, newMessage]);
    setInput('');
    setLoading(true);

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `http://localhost:3000/api/chat/${currentChatId}/message`,
        newMessage,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      console.log('Backend Response:', response.data);

      // Push the bot's response to the chat
      setMessages((prev) => [...prev, response.data]);
    } catch (err) {
      console.error('Error communicating with backend:', err);
      setMessages((prev) => [
        ...prev,
        { role: 'assistant', content: "I'm sorry, I couldn't process your message." },
      ]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchChatHistory();
  }, []);

  return (
    <div className="flex flex-col items-center p-6">
      <h1 className="text-3xl font-bold mb-6">AI ChatBot</h1>
      <button onClick={startNewChat} className="mb-4 bg-blue-500 text-white px-4 py-2 rounded">
        Start New Chat
      </button>
      <div className="flex">
        <div className="w-1/3 p-4 border-r">
          <h2 className="font-bold mb-4">Chat History</h2>
          {chats.map((chat) => (
            <div
              key={chat.id}
              className="cursor-pointer mb-2 p-2 border rounded hover:bg-gray-200"
              onClick={() => {
                setCurrentChatId(chat.id);
                setMessages(chat.messages);
              }}
            >
              Chat {chat.id} - {new Date(chat.createdAt).toLocaleDateString()}
            </div>
          ))}
        </div>
        <div className="w-2/3 p-4">
          <div className="h-96 overflow-y-scroll border mb-4">
            {messages.map((msg, idx) => (
              <div
                key={idx}
                className={`p-2 ${msg.role === 'user' ? 'text-right bg-blue-100' : 'text-left bg-gray-100'}`}
              >
                {msg.content}
              </div>
            ))}
          </div>
          <div className="flex">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type your message..."
              className="flex-1 border px-4 py-2 rounded"
            />
            <button
              onClick={sendMessage}
              className={`ml-4 px-4 py-2 rounded ${
                loading ? 'bg-gray-500 cursor-not-allowed' : 'bg-green-500 text-white hover:bg-green-600'
              }`}
              disabled={loading}
            >
              {loading ? 'Loading...' : 'Send'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatBot;
