import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';

const calculateBudget = () => {
  const income = finances.filter((f) => f.type === 'Income').reduce((sum, f) => sum + f.amount, 0);
  const expense = finances.filter((f) => f.type === 'Expense').reduce((sum, f) => sum + f.amount, 0);

  return { income, expense };
};

const { income, expense } = calculateBudget();

const budgetData = {
  labels: ['Income', 'Expense'],
  datasets: [
    {
      data: [income, expense],
      backgroundColor: ['#4caf50', '#f44336'], // Green and Red
    },
  ],
};


const FinanceTracker = () => {
  const [finances, setFinances] = useState([]);
  const [newFinance, setNewFinance] = useState({
    title: '',
    amount: '',
    type: 'Income',
    category: '',
  });
  const [error, setError] = useState(null);

  const fetchFinances = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:3000/api/finance', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFinances(response.data);
    } catch (err) {
      setError('Unable to fetch finances');
    }
  };

  const addFinance = async () => {
    if (!newFinance.title || !newFinance.amount || !newFinance.category) {
      setError('All fields are required');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      console.log('Sending data:', newFinance); // Log the data being sent
      await axios.post(
        'http://localhost:3000/api/finance',
        newFinance,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setNewFinance({ title: '', amount: '', type: 'Income', category: '' });
      fetchFinances(); // Refresh finance list
    } catch (err) {
      console.error('Error adding finance:', err.response?.data || err); // Log the error
      setError('Unable to add finance');
    }
  };

  const deleteTransaction = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:3000/api/finance/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchFinances(); // Refresh the transactions list
    } catch (err) {
      console.error('Error deleting transaction:', err);
    }
  };



  const sendSummary = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        'http://localhost:3000/api/finance/email-summary',
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Finance summary sent to your email!');
    } catch (err) {
      setError('Unable to send summary');
    }
  };

  useEffect(() => {
    fetchFinances();
  }, []);

  return (
    <div className="flex flex-col items-center justify-center p-6">
      <h1 className="text-3xl font-bold mb-6">Finance Tracker</h1>
      <div className="w-full max-w-lg bg-white p-6 shadow-md rounded-lg">
        <h2 className="text-xl font-semibold mb-4 text-center">Add New Entry</h2>
        <div className="flex flex-col space-y-4">
          <input
            type="text"
            placeholder="Title"
            value={newFinance.title}
            onChange={(e) => setNewFinance({ ...newFinance, title: e.target.value })}
            className="w-full border px-4 py-2 rounded"
          />
          <input
            type="number"
            placeholder="Amount"
            value={newFinance.amount}
            onChange={(e) => setNewFinance({ ...newFinance, amount: e.target.value })}
            className="w-full border px-4 py-2 rounded"
          />
          <select
            value={newFinance.type}
            onChange={(e) => setNewFinance({ ...newFinance, type: e.target.value })}
            className="w-full border px-4 py-2 rounded"
          >
            <option value="Income">Income</option>
            <option value="Expense">Expense</option>
          </select>
          <input
            type="text"
            placeholder="Category"
            value={newFinance.category}
            onChange={(e) => setNewFinance({ ...newFinance, category: e.target.value })}
            className="w-full border px-4 py-2 rounded"
          />
          <button
            onClick={addFinance}
            className="w-full bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Add Entry
          </button>
        </div>
        {error && <p className="text-red-500 mt-4">{error}</p>}
      </div>

      <h2 className="text-2xl font-semibold mt-8 mb-4">Your Finances</h2>
      <div>
        {finances.map((finance) => (
          <div
            key={finance._id}
            className={`border p-4 mb-4 shadow-md ${
              finance.type === 'Income' ? 'bg-green-100' : 'bg-red-100'
            }`}
          >
            <p>{finance.title}</p>
            <p>Amount: ${finance.amount}</p>
            <p>Category: {finance.category}</p>
            <p>Date: {new Date(finance.date).toLocaleDateString()}</p>
            <button
              className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              onClick={() => deleteTransaction(finance._id)}
            >
              Delete
            </button>
          </div>
        ))}
      </div>


      <button
        onClick={sendSummary}
        className="mt-8 bg-green-500 text-white px-6 py-2 rounded hover:bg-green-600"
      >
        Send Email Summary
      </button>
    </div>
  );
};

export default FinanceTracker;
