import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ChatBot = () => {
  const [chats, setChats] = useState([]);
  const [currentChatId, setCurrentChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch Chat History
  const fetchChatHistory = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('User is not authenticated.');
        return;
      }

      const response = await axios.get('http://localhost:3000/api/chat', {
        headers: { Authorization: `Bearer ${token}` },
      });

      setChats(response.data); // Update the chat list
      setError(null); // Clear any previous error
    } catch (err) {
      console.error('Error fetching chat history:', err.response || err);
      setError('Unable to fetch chat history.');
    }
  };


  // Start a New Chat
  const startNewChat = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:3000/api/chat',
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (response.status === 201 && response.data) {
        setCurrentChatId(response.data._id);
        setMessages([]);
      } else {
        setError('Failed to start a new chat. Please try again.');
      }
    } catch (err) {
      console.error('Error starting new chat:', err);
      setError('Unable to start a new chat.');
    }
  };


  // Send a Message
  const sendMessage = async () => {
    if (!input.trim()) {
      setError('Please enter a message.');
      return;
    }
    if (!currentChatId) {
      setError('Please start a new chat first.');
      return;
    }

    const userMessage = { role: 'user', content: input };
    setMessages((prev) => [...prev, userMessage]);
    setInput('');
    setLoading(true);
    setError(null);

    try {
      const token = localStorage.getItem('token');
      await axios.post(
        `http://localhost:3000/api/chat/${currentChatId}/message`,
        userMessage,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const response = await axios.post(
        'https://api.openai.com/v1/chat/completions',
        {
          model: 'gpt-3.5-turbo',
          messages: [...messages, userMessage],
        },
        {
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer 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`,
          },
        }
      );

      const botMessage = {
        role: 'assistant',
        content: response.data.choices[0].message.content,
      };

      await axios.post(
        `http://localhost:3000/api/chat/${currentChatId}/message`,
        botMessage,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setMessages((prev) => [...prev, botMessage]);
    } catch (err) {
      console.error('Error communicating with OpenAI:', err);
      setError('Something went wrong. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchChatHistory();
  }, []);

  return (
    <div className="flex flex-col items-center p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6">AI ChatBot</h1>

      {/* Chat History */}
      <div className="flex w-full max-w-5xl space-x-6">
        <div className="w-1/4 bg-white shadow-md rounded-lg p-4">
          <h2 className="font-bold text-lg mb-4">Chat History</h2>
          {chats.map((chat, idx) => (
            <div
              key={idx}
              className={`cursor-pointer p-2 rounded mb-2 ${
                currentChatId === chat._id ? 'bg-blue-100' : 'hover:bg-gray-200'
              }`}
              onClick={() => {
                setCurrentChatId(chat._id);
                setMessages(chat.messages);
              }}
            >
              Chat {idx + 1} - {new Date(chat.createdAt).toLocaleDateString()}
            </div>
          ))}
          <button
            onClick={startNewChat}
            className="mt-4 w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600"
          >
            Start New Chat
          </button>
        </div>

        {/* Chat Window */}
        <div className="w-3/4 bg-white shadow-md rounded-lg p-4">
          <div className="h-96 overflow-y-scroll border p-4 mb-4 rounded-lg">
            {messages.length === 0 && (
              <p className="text-gray-500 text-center">No messages yet.</p>
            )}
            {messages.map((msg, idx) => (
              <div
                key={idx}
                className={`p-2 mb-2 rounded ${
                  msg.role === 'user'
                    ? 'bg-blue-100 text-right'
                    : 'bg-gray-100 text-left'
                }`}
              >
                {msg.content}
              </div>
            ))}
          </div>
          <div className="flex items-center space-x-4">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type your message..."
              className="flex-1 border px-4 py-2 rounded"
            />
            <button
              onClick={sendMessage}
              className={`px-4 py-2 rounded ${
                loading
                  ? 'bg-gray-500 cursor-not-allowed'
                  : 'bg-green-500 text-white hover:bg-green-600'
              }`}
              disabled={loading}
            >
              {loading ? 'Loading...' : 'Send'}
            </button>
          </div>
          {error && <p className="text-red-500 mt-2">{error}</p>}
        </div>
      </div>
    </div>
  );
};

export default ChatBot;
