import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Homepage = () => {
  const [user, setUser] = useState(null);
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const token = localStorage.getItem('token'); // Get JWT from localStorage
        if (!token) {
          setError('User not logged in');
          return;
        }

        // Fetch user details from backend
        const response = await axios.get('http://localhost:3000/api/user', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(response.data);
      } catch (err) {
        setError('Unable to fetch user details');
      }
    };

    const fetchWeather = async () => {
      try {
        const apiKey = 'YOUR_API_KEY'; // Replace with your OpenWeatherMap API key
        const city = 'Melbourne'; // Default location
        const response = await axios.get(
          `https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=${apiKey}`
        );
        setWeather(response.data);
      } catch (err) {
        setError('Unable to fetch weather data');
      }
    };

    fetchUserDetails();
    fetchWeather();
  }, []);

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="w-64 bg-gray-800 text-white min-h-screen">
        <nav className="flex flex-col p-4">
          <a href="/home" className="mb-4 text-lg font-bold">
            Home
          </a>
          <a href="/tbd" className="mb-4">
            TBD
          </a>
          <a href="/logout" className="text-red-500">
            Logout
          </a>
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col justify-center items-center">
        <h1 className="text-3xl font-bold mb-6">
          Hello, {user ? user.name : 'Guest'}!
        </h1>
        {weather ? (
          <p className="text-lg">
            The weather in {weather.name} is {weather.weather[0].description} with a temperature of {weather.main.temp}Â°C.
          </p>
        ) : error ? (
          <p className="text-red-500">{error}</p>
        ) : (
          <p>Loading weather...</p>
        )}
      </div>
    </div>
  );
};

export default Homepage;
