import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ChatApp = () => {
    const [chats, setChats] = useState([]);
    const [loading, setLoading] = useState(false);

    // Fetch chat history on component load
    const fetchChatHistory = async () => {
        try {
            const response = await axios.get('http://localhost:3000/api/chat');
            setChats(response.data); // Update state with chat history
        } catch (error) {
            console.error('Error fetching chat history:', error);
        }
    };

    // Create a new chat
    const createNewChat = async () => {
        try {
            setLoading(true);
            const response = await axios.post('http://localhost:3000/api/chat');
            setChats((prevChats) => [...prevChats, response.data]); // Add new chat to state
            setLoading(false);
        } catch (error) {
            console.error('Error creating new chat:', error);
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchChatHistory(); // Fetch chats when the component loads
    }, []);

    return (
        <div>
            <h1>Chat App</h1>
            <button onClick={createNewChat} disabled={loading}>
                {loading ? 'Creating...' : 'Start New Chat'}
            </button>
            <ul>
                {chats.map((chat) => (
                    <li key={chat.id}>{`Chat ${chat.id} - ${new Date(chat.createdAt).toLocaleString()}`}</li>
                ))}
            </ul>
        </div>
    );
};

export default ChatApp;
