import React, { useState } from 'react';
import axios from 'axios';

const ChatBot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  const API_KEY = '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';

  const sendMessage = async () => {
    if (!input.trim()) return;

    const newMessage = { role: 'user', content: input };
    setMessages((prev) => [...prev, newMessage]);
    setInput('');
    setLoading(true);

    try {
      const response = await axios.post(
        'https://api.openai.com/v1/chat/completions',
        {
          model: 'gpt-3.5-turbo', // You can also use 'gpt-4' if you have access
          messages: [...messages, newMessage],
        },
        {
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${API_KEY}`,
          },
        }
      );

      const botMessage = {
        role: 'assistant',
        content: response.data.choices[0].message.content,
      };

      setMessages((prev) => [...prev, botMessage]);
    } catch (error) {
      console.error('Error communicating with OpenAI:', error);
      const errorMessage = {
        role: 'assistant',
        content: 'Oops! Something went wrong. Please try again later.',
      };
      setMessages((prev) => [...prev, errorMessage]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center p-6">
      <h1 className="text-3xl font-bold mb-6">AI ChatBot</h1>
      <div className="w-full max-w-2xl bg-gray-100 p-6 rounded shadow">
        <div className="h-96 overflow-y-scroll p-4 border rounded mb-4">
          {messages.map((msg, idx) => (
            <div
              key={idx}
              className={`mb-2 p-2 rounded ${
                msg.role === 'user'
                  ? 'bg-blue-500 text-white self-end'
                  : 'bg-gray-300 text-black self-start'
              }`}
            >
              <p>{msg.content}</p>
            </div>
          ))}
        </div>
        <div className="flex items-center">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type your message..."
            className="flex-1 border px-4 py-2 rounded mr-4"
          />
          <button
            onClick={sendMessage}
            className={`px-4 py-2 rounded ${
              loading
                ? 'bg-gray-500 cursor-not-allowed'
                : 'bg-green-500 text-white hover:bg-green-600'
            }`}
            disabled={loading}
          >
            {loading ? 'Loading...' : 'Send'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatBot;
