import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Homepage = () => {
  const [user, setUser] = useState(null);
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch User Details
    const fetchUserDetails = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setError('User not logged in');
          return;
        }

        const userResponse = await axios.get('http://localhost:3000/api/user', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(userResponse.data);
      } catch (err) {
        console.error(err);
        setError('Unable to fetch user details');
      }
    };

    // Fetch Weather Data
    const fetchWeather = async () => {
      try {
        const apiKey = 'YOUR_API_KEY'; // Replace with your WeatherAPI key
        const city = 'Melbourne';
        const response = await axios.get(
          `https://api.weatherapi.com/v1/current.json?key=${apiKey}&q=${city}`
        );
        setWeather(response.data);
      } catch (err) {
        console.error('Error fetching weather:', err);
        setError('Unable to fetch weather data');
      }
    };

    fetchUserDetails();
    fetchWeather();
  }, []);

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className="w-64 bg-gray-800 text-white min-h-screen">
        <nav className="flex flex-col h-full">
          <h1 className="text-2xl font-bold px-6 py-8 border-b border-gray-700">Custom Web App</h1>
          <a href="/home" className="px-6 py-4 hover:bg-gray-700">
            Home
          </a>
          <button
            onClick={() => {
              localStorage.clear();
              window.location.href = '/';
            }}
            className="px-6 py-4 mt-auto text-left hover:bg-red-600 bg-red-500"
          >
            Logout
          </button>
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col justify-center items-center bg-gray-100">
        <h1 className="text-3xl font-bold mb-6">
          Hello, {user ? user.name : 'Guest'}!
        </h1>
        {weather ? (
          <p className="text-lg">
            The weather in {weather.location.name} is {weather.current.condition.text} with a temperature of {weather.current.temp_c}Â°C.
          </p>
        ) : error ? (
          <p className="text-red-500">{error}</p>
        ) : (
          <p>Loading weather...</p>
        )}
      </div>
    </div>
  );
};

export default Homepage;
